import { Center, Text, VStack, Heading, Box , Button, ButtonGroup, Image } from '@chakra-ui/react';
import React, {useState, useEffect} from 'react';

function MyImage() {
    return <img src="/logobiere.png" alt="Logo" width="10%" height="200px" />;
}

function MyButton() {
    return (
        <Center mt="250px">
            <VStack spacing={4} direction='row' align='center'>
                <Button colorScheme='messenger' size='lg'>
                    LOGIN
                </Button>
            </VStack>
        </Center>
    );
}
const Home = (): JSX.Element => (
<div style={{ backgroundColor: "#000000", height: "20vh" }}>
    <MyImage />
    <Center mt="160px">
     <VStack spacing="32px">
          <Text fontSize='4xl'>Find the beer that suits you</Text>
          <MyButton />
     </VStack>
    </Center>
</div>
);
export default Home;
